INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('ул. Бисерово оз., 23, Вишняковские Дачи, Московская обл., Россия, 142451', '55.768066', '38.118000', 'Биссерово озеро - это большое и мелководное (масимальная глубина до 5 м) озеро Подмосковья, с сильно зарастающей прибрежной частью. Дно его илистое. Много мелкого окуня и плотвы, но в основном для рыбалки на озере интерес представляют карп, подлещик и карась.  Лучшие места ловли на западном берегу, а также в северном и южном оконечностях, где много коряг. Ловят обычно в &quot;окнах&quot; между водной растительностью на червя. Зимой на мормышку и поплавочные удочки на мотыля берут окунь, плотва, ерш. К северо-востоку от озера - пруды рыбхоза. На некоторых из них организована платная рыбалка.  На восточном берегу имеется рыболовно-спортивная база &quot;Биссерово&quot;. База предоставляет спальные места и лодки. Возможно, сейчас база закрыта.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Берхино, Московская обл., Россия, 140478', '54.947653', '38.787231', 'Крутые берега, есть места подхода, но их не так много.<br />Ночью ловля на донку, на червя или кукурузу (голавль, карась, лещь) <br />Днем на спиннинг или нахлыст ( щука, судак, голавль)<br />Рыба есть, но нужно её найти. Летом ставят много сетей. Удобнее всего на лодке');
