INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Краснодарский край, Россия, 353895', '45.885167', '38.106078', 'Арендованный водоем (аренда 49 лет) площадью 146 га. Зарыбленный: карпом, толстолобиком, амуром. Кроме этого в водоеме: сом, щука, тарань, карась, жерих. Рыбалка круглый год, осенняя охота, приятный отдых с друзьями и семьёй. Предлагаемый сервис: ночлег, лодки, снасти. Приглашаем к сотрудничеству, совместному ведению хозяйства. Рассмотрим любые предложения: 5-17-22, 8-928-42-08-655.');
