INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('25, Радутино, Московская обл., Россия, 142340', '55.075223', '37.316780', 'Дорогие рыболовы!<br />Мы организовали рыболовное хозяйство Фанни Фишинг, потому что стремились создать в Подмосковье место, которое можно будет рекомендовать своим друзьям-рыболовам. Мы сами круглый год ловим рыбу в разных уголках России, а в Подмсоковье с удовольствием рыбачим на водоёмах Фанни Фишинг. У нас есть всё, что может привлечь и порадовать настоящего ценителя рыбалки:<br /> <br />    * большой водоём с красивой природой;<br />    * много крупной рыбы ;<br />    * наши платные пруды предоставят Вам самые разнообразные условия ловли, три принципиально разных акватории;<br />    * элементы комфорта и благоустройства органично вписанные в природный ландшафт ;<br />    * максимально гибкая система оплаты за рыбалку;<br />    * прокат и продажа всего необходимого для ловли карпа и другой рыбы;<br />    * консультации по снастям и способам ловли карпа, амура, форели, осётра, щуки, сома, окуня.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Форпост, Астраханская обл., Россия, 416334', '46.029865', '47.828292', 'Сдаю посуточно частные дома на участке 13 сот., расположенном на берегу р. Волга в с. Самосделка (Камызякский район, 40 км ниже Астрахани). В стоимость проживания входит -пользование баней: парная, душ; кухней: газ, вода, посуда; мангалом, коптильней и пр. В доме - тв, холодильник, кондиционер. По желанию за доп. плату – лодки, моторы, питание. Отличные рыбалка, охота, отдых на Нижней Волге.<br />Расценки - минимальные.<br />Подробнее - http://samosdelka.ucoz.ru/index/0-35<br />e-mail: i-isl@rambler.ru, ICQ 481132177, тел. +79170815672, +79163806742');
