INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Большая Свеча, Нижегородская обл., Россия, 606775', '57.692635', '46.292610', 'Ловил тут два раза. Знаю точно что здесь в 2011 вылавливали карпа около 30 кг. Сам я такого не поймал). Ловил в августе приехал на входные, сменилась погода. Поэтому брал только карась, мелкий окунь и плотва. В итоге самым большим трофеем у меня тут стал карась 450 гр. на фидер (кукуруза)');
