INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київська область, Украина', '50.952263', '30.573578', 'Площадь: 200 Га<br />Глубина: 6 м<br />Рыбы: густера, лещ, плотва, окунь, судак, толстолоб, щука, карась, карп<br /><br />Природа очень красивая, сосновый лес, достаточно чисто, строят много вымосток.<br />Очень много муравьев.<br /><br />Контакты: Тел. 096 623 06 59 Николай.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Богданівка, Кіровоградська область, Украина', '48.786000', '32.478300', 'Площадь: 20 Га<br />Глубина: 4 м<br />Рыбы: судак, щука, амур, карась, карп<br /><br />Одна сторона так сказать полевая, другая лесистая (посадка). По лесистой стороне 8 вымосток (в среднем 3х3), по луговой стороне 7 мест (навес) и 4 вымостки, одна из них генеральская 4х6.<br /><br />Контакты: Анатолий Владимирович<br />тел. (067)3086870, (099)1471453');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київська область, Украина', '50.029300', '30.224200', 'Площадь: 15 Га<br />Глубина: 2 м<br />Рыбы: толстолоб, амур, карась, карп, окунь, плотва<br /><br />Места для ловли на любой вкус есть от комфортных с вымосткой до экстремальных в камышах.<br /><br />Контакты: 80676568565');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('вулиця Лесі Українки, Київська область, Украина', '49.956100', '30.154600', 'Площадь: 10 Га<br />Глубина: 5 м<br />Рыбы: линь, окунь, толстолоб, щука, амур, карась, карп<br /><br />для рыбалки оборудованы вымостки, есть сектора, где можно ловить с берега. Можно по телефону забронировать сектор, изучив сначала водоемы.<br />Много рыбаков, которые, приехав раз, приезжают на озера постоянно.  <br />© ОООРуна Рыбные места. Путеводитель.™  2005-2006 г.<br />© Вадим Корунский<br /><br />Контакты: Вадим (097) 355 10 05');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київ, Украина', '50.466900', '30.558900', 'Площадь: 1000 Га<br />Глубина: 10 м<br />Рыбы: лещ, плотва, окунь, судак, щука<br /><br />Хорошое место для зимней рыбалки в черте города. Добираюсь пешком от Московского моста.');
