INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('вулиця Миколи Ушакова, 1Г, Київ, Украина', '50.457941', '30.332565', 'Площадь: 40 Га<br />Глубина: 2 м<br />Рыбы: окунь, карась, карп<br /><br />со стороны города - пляж,<br />но с другой стороны - лес и удобно ловить.<br />Правда маленькая глубина - до 2м, поэтому приходится далеко забрасывать.<br />Дно песчаное +3см ила.<br /><br />Контакты: 0962288540 Андрей');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Житомирська область, Украина', '50.115600', '28.686700', 'Площадь: 16 Га<br />Глубина: 5 м<br />Рыбы: раки, плотва, окунь, сом, толстолоб, щука, амур, карась, карп, линь<br /><br />машину можно ставить возле места рыбалки,подъезд отличный,водоем очень красивый и много рыбы,водоем не спускался 5 лет,берег чистый,удобный для установки палатки. Одним словом место отличное.<br /><br />Контакты: 067-412-39-49<br />Ярослав');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '48.655800', '32.475500', 'Площадь: 8 Га<br />Глубина: 2 м<br />Рыбы: раки, окунь, сом, судак, толстолоб, щука, амур, карась, карп<br /><br />водоем зарыбляется каждый год, в 2008 г. запустили 1 тонну карпика, в 2009 г. - 700 кг. Запускают рыбу весом около 50 гр. Но особи свыше 1 кг попадаются часто (смотрел на результаты сидящих на берегу рыболовов).  Уже несколько лет охранники бурятся с клещом, травят известью, но пока безуспешно... Кормят рыбу регулярно, но на противоположном берегу, там, где ловить нельзя... <br />Из общения с рыбаками: ловили карпа на 7 кг, сома на 9 кг, щуку на 7600гр (рыболовы не врут)');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('T1028, Дідівщина, Київська область, Украина', '50.146986', '29.775009', 'Площадь: 12 Га<br />Глубина: 1 м<br />Рыбы: окунь, толстолоб, амур, карась, карп<br /><br />водоем не спускался 8 лет. Места лова в камышах, их не очень много. <br />Место вроде ничего, можно пробовать. Начали убирать мусор.<br />Осенью дозарыбили карпом 1 т., карп от 300 гр.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '48.590900', '32.944600', 'Рыбы: карась, щука, окунь, плотва, раки, линь<br /><br />Место находится между Новой Прагой и Димитрово. Дорога как раз пересекает реку. Так, что можно приезжать в любую погоду. Место привлекательно для любителей спиннинга. Щука основной объект рыбалки. Но есть и белая рыба (плотва, линь, красноперка, густера). Если ехать из Кировограда, то справа будет более глубокая часть реки, слева соответственно мелкая. В правой части есть очень широкое место, тут можно (лучше) ловить с лодки. Особенно хорошо тут поздней осенью. Дальше (вниз по течению) есть и очень глубокие места. Но основная рыбалка все же лучше не далеко от моста и слева аж до вершины. Щука берет небольшая до 3-х кг. Но есть экземпляры и побольше. Самую большую я поймал на 7,2 кг, на серебристый кастмастер. Основной (лучший) период клева начинается с начала августа, рано утром, а в пасмурные дни и весь день. Вечером намного хуже. В прозрачной воде лучшими приманками являются воблеры и силиконовые приманки, в период цветения - вертушки и колебалки. К месту, где указана рыбка, можно подъехать по грунтовке, справа будет каръерчик, за ним можно съехать аж до воды (внимание! в дождь машину лучше оставить наверху, а то и возле моста). Еще: справа от моста есть старая турбаза (очень небольшая, не знаю рабочая или нет, но сторожа там есть), очень красивое место для отдыха, только дорога туда мягко говоря плохая.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('вулиця Богдана Хмельницького, 100, Кіровоград, Кіровоградська область, Украина', '48.556500', '32.231900', 'Рыбы: щука<br /><br />ул.Б.Хмельницкого между №80.-№82 в право есть дорога метров 70.сплав хороший.<br /><br />Контакты: речка');
