INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київ, Украина', '50.512500', '30.556600', 'Рыбы: линь, лещ, плотва, окунь, сом, щука, карась<br /><br />отличное место для ловли фидером ночью. Клев с 23 до 8 утра, зависит от воды которую подают с ГЭС. Ловится подлещик, плотва, крупный карась, редко попадается линь и клепец(белоглазка).<br /><br />Контакты: ---');
