INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Світлопіль, Кіровоградська область, Украина, 28041', '48.602800', '32.802600', 'Рыбы: линь, раки, плотва, окунь, судак, толстолоб, щука, амур, карась, карп<br /><br />по сути это речка Бешка, перегороженная дамбой в селе Светлополье. Аренды соответственно никакой нет, как и контроля за рыбными ресурсами тоже. <br />Много щуки, карася и карпа. Карп заходит сюда из Мошорино и Пантазиевки.  <br />Отличное место для отдыха и рыбалки.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Центральная, Верем&#039;я, Київська область, Украина', '50.053500', '30.809000', 'Площадь: 30 Га<br />Глубина: 5 м<br />Рыбы: толстолоб, щука, амур, карась, карп<br /><br />места для лова – практически по всей береговой линии. На левом берегу есть беседки со столиками. Отличное место для отдыха всей семьёй.<br />Каждый год пруд хорошо зарыбляется карпом, белым амуром и толстолобиком на вырост.');
