INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '48.680700', '32.302900', 'Площадь: 120 Га<br />Глубина: 3 м<br />Рыбы: щука, карась, карп<br /><br />В конце дамбы справа обрывистый берег без камыша там и занимаем места. Проехать можно в любую погоду.<br /><br />Контакты: ТОВ АгроДар (0522)32-15-17 Ирина Семёновна');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '48.441273', '32.197323', 'Площадь: 25 Га<br />Глубина: 5 м<br />Рыбы: плотва, окунь, судак, толстолоб, амур, карась, карп<br /><br />водоем большой и глубокий, но мест для ловли мало, а для ловли на фидер еще меньше. Берег очень зарос камышом, причем с обеих сторон.');
