INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київська область, Украина', '50.251294', '30.746140', 'Площадь: 1000 Га<br />Глубина: 4 м<br />Рыбы: окунь, щука, карась<br /><br />Канал не очень широкий, при желании можно добросить до противоположного берега. С лодок тоже ловят, плюс если поплыть в сторону Киева он расширяется.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київська область, Украина', '50.062263', '30.321922', 'Площадь: 8 Га<br />Глубина: 4 м<br />Рыбы: окунь, карась, карп<br /><br />Водоём не спускался давно, нету возможности.<br />Взят в аренду и открыт для рыбалки с 1 мая 2010 г.<br />Мест для ловли много, практически весь периметру.<br />По словам местных, в водоёме присутствует крупный карась до 500-700 гр., крупный окунь и короп.<br />В середине апреля этого года зарыблено коропом :<br />0,5 т : 3-7кг и 1 т : 0,5 - 3 кг<br />Планируется развитие инфраструктуры.<br /><br />Контакты: 0675017707 – Ярослав<br />0969783798 – Александр');
