INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('SP99, 53018 Sovicille SI, Италия', '43.214559', '11.257381', 'Площадь: 1 Га<br />Глубина: 1 м<br />Рыбы: угорь, голавль, плотва, сом, щука, карась, карп<br /><br />речушка является продолжением реки МЕрсе, перегороженной дамбой для орошения полей. После мини-дамбы образуется затон со спокойной (относительно) водой. <br />Дополнительная информация : http://www.provincia.siena.it/upload/tbl_centrale/pol_prov_calendario_ittico_2009_.pdf<br /><br />Контакты: нужно иметь лицензию (на год-34 евро)');
