INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('вулиця Снайпера, Гореничі, Київська область, Украина', '50.439755', '30.215836', 'Площадь: 5 Га<br />Глубина: 5 м<br />Рыбы: плотва, окунь, щука, карп<br /><br />На обычной карте показано что там один водоем но там их 3 смотрите на спутниковой карте(до длинного нет прохода). Щуки и плотвы больше на маленьком озере, а карпа на большом.');
