INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Т1019, Шибене, Київська область, Украина', '50.733683', '30.019620', 'Площадь: 3 Га<br />Глубина: 3 м<br />Рыбы: карась, карп<br /><br />Стоимость 50грн день, 30грн пол дня. Неделю назад зарыблен 100-150гр коропом. Зарыбок, конечно, жадно берет. Озеро находится возле оживленной внутрисельской дороги, шумно.');
