INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '48.867820', '32.886629', 'Площадь: 8 Га<br />Глубина: 2 м<br />Рыбы: карась<br /><br />Водоём большой, но мест не много, берега заросшие. Рыбалку осложняет трава (водоросли), которая везде.');
