INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '48.431365', '31.156111', 'Площадь: 90 Га<br />Глубина: 6 м<br />Рыбы: раки, плотва, окунь, толстолоб, амур, карась, карп<br /><br />Водоём очень перспективный, в аренде около 15 лет был закрыт для рыбалки, но в этом году начинают пускать есть карпы за 10кг., зарыбляется каждый год рыбы много.<br /><br />Контакты: Отловочные билеты на месте.');
