INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Р63, Беларусь', '54.484001', '27.070999', 'Площадь: 6300 Га<br />Глубина: 14 м<br />Рыбы: карп, карась, щука, судак, окунь, плотва, лещ, раки, линь, густера<br /><br />Очень много людей приезжают на водоём, как зимой так и летом.<br /><br />Контакты: Часть водоёма арендовано, но любительский лов, бесплатно');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('вулиця Курортна, Черкаська Лозова, Харківська область, Украина', '50.114827', '36.220980', 'Площадь: 30 Га<br />Глубина: 4 м<br />Рыбы: карп, карась, амур, щука, толстолоб, судак, окунь, плотва, лещ, линь, густера<br /><br />Ставят сети, но рыбы много и разной. Береговая линия длинная, много мест. Есть мостики.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Донецька область, Украина', '47.106530', '37.352915', 'Площадь: 8 Га<br />Глубина: 5 м<br />Рыбы: раки, плотва, окунь, толстолоб, щука, амур, карась, карп<br /><br />Водоем в аренде относительно недавно.Проезд к водоему по грунтовке (порядка 5 км.), что в дождливую погоду может сыграть злую шутку. По периметру водоем сильно зарос камышом. Подходов к воде пока мало, однако хозяева начинают строить вымостки. На берегу со стороны домика охраны есть вагончики и столики. В общем - удобства пока минимальные, но есть перспектива.<br />Дно илистое, местами слой ила достигает 20 см. В водоеме очень много щуки.<br />По непроверенным данным возможна ловля с лодки - 100 грн. с рыбака.<br /><br />Контакты: Отсутствуют');
