INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Донецька область, Украина', '47.964526', '37.454710', 'Площадь: 70 Га<br />Глубина: 5 м<br />Рыбы: раки, окунь, судак, толстолоб, амур, карась, карп<br /><br />Ловля с плит, места под рыбака удобно оборудованы и есть мостики в камышах.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Н03, Житомирська область, Украина', '50.207835', '28.423219', 'Площадь: 50 Га<br />Глубина: 8 м<br />Рыбы: щука, окунь, плотва, лещ, раки, густера<br /><br />Удобные места для ловли - вдоль всего берега небольшой камыш, много прочищеных мест. Удочкой с берега (на личном опыте убедился) клюет в основном мелочь - изредка подходит полукилограмовый подлещик и крупная плотва (хотя человек, который порекомендовал мне это место, ловил тут достойные трофеи амура и леща именно на обычную 4 метровую удочку с мелким крючком на мотыля). В основном более крупная мирная рыба берет тут на донки и фидеры на кормушки и макуху. Подкормку использую обычно ФИШДРИМ КАРП без всяких домесей - работает тут превосходно на всю рыбу. Из насадок на дальний заброс в основном опарыш, изредка перловка (червяка без клева постоянно что-то объедает полностью без поклевок - склонен грешить на рака). На удочку самая уловистая наживка мотыль, тесто и мастырка. Намного реже берет опарыш, перловку и червя.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('T0107, Крым, Украина', '45.835587', '33.501005', 'Площадь: 18 Га<br />Глубина: 1,2 м<br />Рыбы: карась, карп, сом, белый амур.<br /><br />Водоем был хороший. 14 ставков. Из-за отсутствия воды в северо-крымском канале делать там нечего ставки пустые.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Нова Прага, Кіровоградська область, Украина', '48.600339', '32.916241', 'Площадь: 8 Га<br />Глубина: 10 м<br />Рыбы: раки, плотва, окунь, сом, судак, толстолоб, щука, амур, карась, карп<br /><br />много толстолобика, но клевать не хочет ,есть крупный сом и крупный карп. Запущен зеркальный карп, которого при поимке надо выпускать. Очень много комаров, в связи с этим ночёвка затруднительная');
