INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Донецька область, Украина', '48.186633', '37.769537', 'Площадь: 1 Га<br />Глубина: 10 м<br />Рыбы: карп, карась, толстолоб<br /><br />Ловить в будние дни!!!!!<br />Имеют моду спускать воду на выходные. Рыба болеет. Ни одной поклевки. Лучше ехать в будние дни. Ловить возле дамбы и в месте спуска воды. Занимать места лучше заранее.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('P09, Маслівка, Київська область, Украина', '49.711105', '31.166840', 'Площадь: 70 Га<br />Глубина: 3 м<br />Рыбы: карп, карась, щука, толстолоб, окунь, раки, линь<br /><br />Водоем большой и очень красивый. Со стороны леса можно поставить палатки и наслаждаться природой и ловлей рыбы. <br />Ну а минус это большое количество мусора.<br /><br />Контакты: Водоем общего пользования');
