INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Синицівка, Кіровоградська область, Украина, 26433', '48.209575', '30.192490', 'Площадь: 40 Га<br />Глубина: 10 м<br />Рыбы: карп, карась, толстолоб, плотва, раки<br /><br />озеро представляет собой ущелье в каменной породе, дно каменное изредка песок, берега крутые скалистые<br />озеро было создано как водохранилище <br />одна из особенностей пруд каждую осень спускают для вылова рыбы, рыбу там не разводят она сама там растет)<br /><br />Контакты: - они подплывают на лодке');
