INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '48.466007', '32.126083', 'Площадь: 8 Га<br />Глубина: 3 м<br />Рыбы: карп, карась, амур, толстолоб, окунь, плотва, раки<br /><br />Не очень глубокий водоем с большим количеством мест. Очень много мелкого карася.<br />И незнаю кому как, потому что даже когда водоем был в аренде рыбаков было море, но вкус рыбы отвратительный.');
