INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Черкаська область, Украина', '49.562100', '31.775700', 'Площадь: 40 Га<br />Глубина: 15 м<br />Рыбы: карп, карась, сом, толстолоб, амур, щука, судак, жерех, окунь, плотва, лещ, раки, линь<br /><br />Уникальная речка! Правая притока Днепра (Кременчутское водохранилище)<br />Подьездов к реке очень много! Мест для ловли с берега достаточно! По всему берегу растут кувшинки,возможны проблемы ловли с берега!<br />Природа очень красивая,чисто,мало людей.<br />так же есть канал.Тоже очень хорошо ловиться БЕЛь.<br /><br />Контакты: ____');
