INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '47.827900', '32.013200', 'Площадь: 10 Га<br />Глубина: 2 м<br />Рыбы: раки, плотва, окунь, карась, карп<br /><br />Проезд:  ехать по Бобринецкой трассе до поворота на Тарасовку, поворот вправо и по грунтовой, но отсыпаной жорствой дороге, дальше налево мимо Байкала, через дамбу и по правой стороне полем . Ехать можно только в сухую погоду. <br />Берег пологий ловиться постоянно.<br /><br />Контакты: Ставок дикий не в аренде');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('вулиця Бережна, 273, Дніпро́, Дніпропетровська область, Украина', '48.554300', '34.906100', 'Площадь: 25 Га<br />Глубина: 3 м<br />Рыбы: карась, окунь, раки, линь<br /><br />Конец ул Передовой. Конечная маршрутки №4.<br />Озеро недавно было очищено. Глубины сразу возле берега. Берег чистый. Вода на удивление тоже.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київська область, Украина', '50.629700', '31.038800', 'Площадь: 2 Га<br />Глубина: 1 м<br />Рыбы: окунь, щука<br /><br />Два маленьких озера, заросшие травой. И в одном и во втором озере много мелкой щуки. Видел выход одной большой щуки. Много мелкого окуня, гоняющего стайками, но он на столько мелкий, что мепсовская вертушка №0, для них велика....))))<br /><br />Контакты: владельца нет');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кудашівка, Дніпропетровська область, Украина, 53052', '47.954700', '33.101900', 'Площадь: 5000 Га<br />Глубина: 5 м<br />Рыбы: карп, карась, щука, плотва, раки, линь<br /><br />перед остановкой Кудашево поворот на лево,проежаем село и сразу на право(сьезд к реке). Берег чистый.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Кіровоградська область, Украина', '48.565100', '30.766600', 'Площадь: 50 Га<br />Глубина: 6 м<br />Рыбы: судак, щука, толстолоб, сом, карась, карп<br /><br />Проезд:  если ехать с Кировограда то в сторону Умани, то недоезжая Новоархангльска, слева пост ГАИ (действующий!!!), справа заправка, за ними через 100 м отворот налево мимо еще одной заправки по асфальту до заброшенного села (едем смело), потом смотрим съезд в лес через шлагбаум, через 10м поворот направо к пляжу и места отдыха, если прямо - к местам рыбалки, но выходов к воде мало. Если же не съезжать с асфальта то еще через 3км ГЭС через плотину на другой берег. Если нет дождя, то раздолье и мест много, и рыбы.<br />Рыба ловится на естественные насадки, между поклевками карася и леща на фидер можно развлекаться красноперкой на поплавок.<br /><br />Контакты: Государственный водоем');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Тернопільська область, Украина', '49.427800', '25.484300', 'Площадь: 1 Га<br />Глубина: 2 м<br />Рыбы: карп, карась, окунь, плотва, линь<br /><br />На краю села небольшой водоём.<br />Рыбка мелкая, но ловится хорошо.<br />Глубина на дамбе.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('вулиця Горького, Червоне, Київська область, Украина', '50.014600', '30.383500', 'Площадь: 4 Га<br />Глубина: 2 м<br />Рыбы: карп, карась, плотва<br /><br />трасса Васильков Саливонки(по Обуховской трассе),левый поворот на Яцки(вьезд в село) по селу через 1250м круто в лево(ориентир проезд между двемя заборами из листового шифера) едем по каменке до конца поворот на право и вдоль села по этой дороге,которая выведет к шлангбауму,дальше по полевой к домику.Став для ловли дальний видно из дамика.Дно твердое,ширина 60м в вверховье,до 80 к дамбе.глубины возле дамбы по маркеру 2,5м.Ловился карась и потвичка(детская).Свой маточник,но гулянья карпа не было.Хозяин сказал,что есть от 600гр до3,5кг.Нужно еще раз выехать для точного определения.Мечто хорошо тем что не сидят на головах,мест на любом берегу для рыбалки много.<br /><br />Контакты: не дал,всегда на месте');
