INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Полтавська область, Украина', '49.640900', '34.487000', 'Площадь: 1 Га<br />Глубина: 3 м<br />Рыбы: карп, карась, амур<br /><br />Едем из Полтавы в сторону Диканьки, сразу за Автоагрегатным заводом налево, транзитом через Ивонченцы, на конечной остановке налево и через метров 100 по бетонке направо. слева увидите ставки. Хозяев много разных. билет на сутки от 20грн до 35грн. Довольно чисто.<br /><br />Контакты: Охрана обилечивает на месте.');
