INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Білашів, Рівненська область, Украина', '50.338900', '26.392400', 'Площадь: 3 Га<br />Глубина: 2 м<br />Рыбы: карп, карась, толстолоб, амур<br /><br />Дорога ужасная+перечень рыбы - это точто обещали,но ее как таковой нет!!!А если есть то очень мало для такого водоема!!!<br /><br />Контакты: 80983756434');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Новокраснянка, Луганська область, Украина', '49.138200', '38.299000', 'Площадь: 12 Га<br />Глубина: 3 м<br />Рыбы: амур, толстолоб, карась, карп<br /><br />Особенности водоема:  4-ый ставок. Крутые берега. Сильно илистое дно. Очень мало удобных мест для стоянки авто. <br />Есть крупный сазан до 6-9 кг. Ловят в основном на горох (сазан). Карась мелкий. <br />Много мусора на местах ловли, уборкой никто не занимается.');
