INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('О180307, Малинівка, Рівненська область, Украина', '50.664400', '26.677700', 'Площадь: 20 Га<br />Глубина: 2 м<br />Рыбы: карась, карп<br /><br />Проезд: Ехать от Ровно в сторону Киева. За мостом через Горыть поворот налево. Проезжаем Чудныцю и Воскодавы. Далее - направо. Через саму Малиновку - разбитая. При дождливой погоде с дамбы сьехать практически невозможно.');
