INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Мілова Балка, Кіровоградська область, Украина', '48.687300', '32.464000', 'Площадь: 5 Га<br />Глубина: 4 м<br />Рыбы: плотва, окунь, толстолоб, карась, карп<br /><br />По знаменской трассе,повернуть наОрлиное Гнездо,проехать,перед ореховым садом отворо вправо на полевую дорогу по ней прямо,налево,направо,через 10м перд лесополоской налево,до т-обзного перекрестка,напрво и вьесжаете в село,справа платник,слева безплатник.<br />Рыбы в ставке много,мног мелкого карпика который уже два года не растет,но судя по скидывающимся есть и хорошие экземпляры.<br />Ловить разрешает только со стороны села,хотя основные скидывания набдюдаются с противоположного берега.<br />На фидер разрешено  только в двух местах.<br />Корче арендатор зажрался предлагаю водоем байкотировать.<br /><br />Контакты: 099 67 06 979 - Саша');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Дрозди, Київська область, Украина', '49.896900', '29.977100', 'Площадь: 10 Га<br />Глубина: 2 м<br />Рыбы: окунь, щука, карась<br /><br />Проезд:  трасса Фастов - Белая Церковь, направо село Дрозды (указатель), напротив фермы (проезд со стороны Фастова) съезд направо (почти параллельная дорога), первый поворот направо и по асфальту. Виден берег - ширина речки 20 м.<br />По ранней весне щучки от 700 гр. и больше. А в дальнейшем карась, окунь. Со слов местных - они не разрешают браконьерничать и следят за речкой.');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('вулиця Юрія Гагаріна, Пологи, Київська область, Украина', '49.908800', '30.088900', 'Площадь: 18 Га<br />Глубина: 2 м<br />Рыбы: карп, карась, окунь<br /><br />По ранней весне и зимой прекрастное места для ловли окуня,есть хорошие экземпляры(блесна)<br />летом карп,карась.Подьезд к озеру асвальт,орриентир церковь.т.к селе три озера<br /><br />Контакты: не нашел,а сторож телефон не дал');
INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київська область, Украина', '50.067200', '30.344300', 'Площадь: 4 Га<br />Глубина: 2 м<br />Рыбы: окунь, карась<br /><br />Проезд:  трасса Васильков -Погребы-Саливонки, въезд в село Тростинка, второй спуск направо к кладбищу, проезжаем ферму (развалины) в поле, внизу небольшое озеро. Машину оставляем наверху.<br />Карась до 500 гр., окунь.<br /><br />Контакты: пока нет владельца');
