INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('P05, Рівненська область, Украина', '50.964300', '26.546800', 'Площадь: 6 Га<br />Глубина: 2 м<br />Рыбы: окунь, карась, карп<br /><br />Проезд: от Ровно на север за Костополем в сторону Сарн около 20 км. <br />2 пруда. Зарыблены карпом, карасем. Есть окунь.');
