INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Донецька область, Украина', '48.540400', '37.501200', 'Площадь: 12 Га<br />Глубина: 3 м<br />Рыбы: карп, карась, амур<br /><br />По трассе Дружковка-Доброполье в селе Райском поворот налево там первый ставок проезжаем и по грунтовке полем первый поворот налево Юго-западный берег пологий и мелко, противоположный отвесной и глубже. Редкие одинокие деревья.<br /><br />Контакты: Дружковка');
