INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Львівська область, Украина', '49.601100', '24.646400', 'Площадь: 3 Га<br />Глубина: 3 м<br />Рыбы: амур, карась, карп<br /><br />проїзд з Перемишлян доїчати до Брюхович перший поворот на право проїжаєм через Костенів , Білку їдем польовою дорогою до самого ставу. Природа тут супер. В ставку водиться форель 30-40 см<br /><br />Контакты: немаю');
