INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('E583, Вінницька область, Украина', '49.527900', '28.546700', 'Площадь: 30 Га<br />Глубина: 4 м<br />Рыбы: раки, плотва, окунь, толстолоб, щука, амур, карась, карп<br /><br />Места для ловли есть на обеих берегах.<br />В июле 2009 года поймали карпа ок. 10 кг.<br />Ежегодные взносы идут на зарыбление и благоустройство водоёма. Рыболовы сами контролируют порядок.');
