INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Т2403, Черкаська область, Украина', '49.323200', '30.981800', 'Площадь: 18 Га<br />Глубина: 25 м<br />Рыбы: карп, карась, амур, щука, окунь<br /><br />Хозяин хочет стать конкурентом соседней Шендеровки - в этом году начал пускать ловить рыбу за деньги. Озеро неглубокое. Рыбаков мало. Берега поросли камышом. Охранник - консультирует и подсказывает.<br />Подъезд - асфальт почти до самого озера.<br />На фото слева у камышей видна утянутая удочка... к охраннику приехали друзья и они слегка расслабились. Когда я подошел, как раз спорили - кому лезть в воду!<br /><br />Контакты: Леонид 8 098 6299942');
