INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Е40, Харківська область, Украина', '49.689781', '36.838682', 'Площадь: 20 Га<br />Глубина: 6 м<br />Рыбы: раки, толстолоб, амур, карась, карп<br /><br />Озеро имеет два рукава, за что и получило своё название. Один рукав узкий и не глубокий, второй глубокий и около дамбы сильно закоряжен(отличное место для ловли карпа). Вдоль всего водоема можно проехать на машине. Место тихое. Полно мест для разбивки лагеря. Вдоль берега лесовая посадка.');
