INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Unnamed Road, Київська область, Украина', '50.345515', '30.468693', 'Площадь: 3 Га<br />Глубина: 6 м<br />Рыбы: окунь, карась<br /><br />Озеро находится в с.Хотов, Киево-Святошинского р-на, Киевской обл.<br />Озеро бесплатное, зеркало сильно заросшее кувшинками. В 1984 году выбирали торф-поэтому есть глубины, в основном по центру. В 200 году в озеро обрушилась часть прилегающей горы, в итоге с одной стороны става мелковато.');
