INSERT INTO places (place_name, place_geo_lat, place_geo_long, place_descript) VALUES('Лесная ул., 5, Майкоп, Республика Адыгея, Россия, 385012', '44.589734', '40.112114', 'Река Белая (Адыгея) – крупнейшая водоносная жила региона, является одним из мощнейших левобережных притоков Кубани. Протяженность реки составляет более 260 километров. Общее падение, то есть разница между абсолютной высотой устья и истока, составляет 2283 метра, а падение на 1 километр – 840 см (для сравнения: у Дуная – 24 см, а у Волги – 7 см). Верховья реки Белой питают горные ручьи и родники Фишта, Оштена и Абаго. На своем пути воды реки Белой принимают в себя 3460 притоков, самыми крупными из которых являются Дах, Киши, Пшеха и Курджипс.<br />Читать подробнее: http://vetert.ru/rossiya/adygeya/sights/259-reka-belaya.php');
